	float minX = -15.5f;
	float minY = -3.0f;
	float maxX = 10.0f;
	float maxY = -25.0f;
	int dragonsPicked = 0;
	int totalDragons = 2;
	int columns = 51;
public:
	int BlockState(float x, float y)
	{
		return Boundaries[FindIndex(x, y)];
	}

	void ActivateTrap(float x, float y, int state)
	{
		Boundaries[FindIndex(x, y)] = state;
	}

	void SetBlockState(float x, float y, int state)
	{
		Boundaries[FindIndex(x, y)] = state;
	}

	void PickDragon() {
		dragonsPicked++;
		if (dragonsPicked >= totalDragons)
			std::cout << "All dragons picked" << std::endl;
	}

	void SetBlockStateByIndex(int index, int state)
	{
		Boundaries[index] = state;
	}

	glm::ivec2 CoordsToIJ(float x, float y)
	{
		int i = glm::floor(2 * (x - minX));
		int j = glm::floor(2 * (y - minY));
		return glm::ivec2(i, j);
	}

	int FindIndex(float x, float y)
	{
		int i = glm::floor(2 * (x - minX));
		int j = glm::floor(2 * (y - minY));
		int index = j * columns + i;
		/*std::cout << "x = " << x << "y = " << y << std::endl;
		std::cout << "i = " << i << "j = " << j << std::endl;
		std::cout << "index" << index << std::endl;
		std::cout << "--------" << std::endl;*/
		return index;
	}


